import React, { Component } from 'react'
import axios from "axios"
export default class home extends Component {

    state = {
        users: [
        ],
        champions: [
        ],
        maxmastery: [
        ],
        championList:[
        ],
        lista:[],
        Campeon:[],
        imagen:[]
    }





    render() {
        function busqueda() {
            const res =  axios("http://localhost:4000/perfil/jkya")
            const champs =  axios("http://ddragon.leagueoflegends.com/cdn/10.7.1/data/en_US/champion.json")
            const champmasterymax =  axios("http://localhost:4000/perfil/maxmastery/"+res.data.id)
            this.setState({ users: res.data })
            this.setState({champions: champs.data})
            this.setState({maxmastery: champmasterymax.data[0].championId})
            this.setState({championList: this.state.champions.data})
            const url = 'http://ddragon.leagueoflegends.com/cdn/10.7.1/data/en_US/champion.json'

          let dataSource = [];

          Object.values(champs.data.data).forEach(item => {
              dataSource = dataSource.concat(item);
          });

          this.setState({lista: dataSource})
          console.log(this.state.lista)
          console.log(this.state.maxmastery)
          this.state.lista.forEach((item, i) => {
            if (item.key == this.state.maxmastery) {
              return this.setState({ Campeon: item });
            }
          })
          this.setState({imagen: require("./dragontail-10.7.1/img/champion/splash/"+this.state.Campeon.id+"_0.jpg")})
          console.log(this.state.Campeon);
        }
        return (
            <div>
                <div>
                <h1>Buscador</h1>
                <input
                                type="text"
                                className="form-control"
                                placeholder="Title"
                                onChange={this.onInputChange}
                                name="title"
                                value={this.state.title}
                                required />
                <button onClick={busqueda} className="btn btn-primary">Buscar </button>
            </div>
                <div className="container">
                    <div className="card mb-3">

                        <img src={this.state.imagen} className="card-img-top"/>
                            <div className="card-body">
                                <h5 className="card-title">{this.state.users.name}</h5>
                                <p className="card-text">Nivel {this.state.users.summonerLevel}</p>
                                <p className="card-text"><small className="text-muted">{this.state.Campeon.title}</small></p>
                            </div>
                    </div>

                        <footer className="pt-4 my-md-5 pt-md-5 border-top">
                            <div className="row">
                                <div className="col-12 col-md">
                                    <small className="d-block mb-3 text-muted">Â© 2017-2019</small>
                                </div>
                                <div className="col-6 col-md">
                                    <h5>Features</h5>
                                    <ul className="list-unstyled text-small">
                                        <li><a className="text-muted" href="#">Cool stuff</a></li>
                                    </ul>
                                </div>
                                <div className="col-6 col-md">
                                    <h5>Resources</h5>
                                    <ul className="list-unstyled text-small">
                                        <li><a className="text-muted" href="#">Resource</a></li>
                                    </ul>
                                </div>
                                <div className="col-6 col-md">
                                    <h5>About</h5>
                                    <ul className="list-unstyled text-small">
                                        <li><a className="text-muted" href="#">Team</a></li>
                                    </ul>
                                </div>
                            </div>
                        </footer>
                    </div>
                </div>
        )
    }
}
