import React, { Component } from 'react'
import axios from "axios"
export default class home extends Component {

    state = {
        users: [
        ],
        champions: [
        ],
        maxmastery: [
        ],
        championList:[
        ],
        lista:[],
        Campeon:[],
        imagen:[]
    }

    async componentDidMount() {

        const res = await axios("http://localhost:4000/perfil/abidin")
        const champs = await axios("http://ddragon.leagueoflegends.com/cdn/10.7.1/data/en_US/champion.json")
        const champmasterymax = await axios("http://localhost:4000/perfil/maxmastery/"+res.data.id)
        this.setState({ users: res.data })
        this.setState({champions: champs.data})
        this.setState({maxmastery: champmasterymax.data[0].championId})
        this.setState({championList: this.state.champions.data})
        const url = 'http://ddragon.leagueoflegends.com/cdn/10.7.1/data/en_US/champion.json'

      let dataSource = [];

      Object.values(champs.data.data).forEach(item => {
          dataSource = dataSource.concat(item);
      });

      this.setState({lista: dataSource})
      console.log(this.state.lista)
      console.log(this.state.maxmastery)
      this.state.lista.forEach((item, i) => {
        if (item.key == this.state.maxmastery) {
          return this.setState({ Campeon: item });
        }
      })
      this.setState({imagen: require("./dragontail-10.7.1/img/champion/splash/"+this.state.Campeon.id+"_0.jpg")})
      console.log(this.state.Campeon);
    }


    render() {
        return (
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark p-3">
                <div className="container">
                    <Link className="navbar-brand" to="/">
                        <i className="material-icons">
                            assignment </i> NotesApp
                    </Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav ml-auto">
                            <li className="nav-item active">
                                <Link to="/" className="nav-link">Notes</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/create" className="nav-link">Create Note</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/user" className="nav-link">Create User</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        )
    }
}
